import { TableColumn } from '@swimlane/ngx-datatable';
import { ColorRange } from './color-range';
export interface DatatableColumnTooltip {
    column?: string;
    container?: string;
    placement?: 'left' | 'right' | 'top' | 'bottom';
    showCondition?: (row?: any) => boolean;
    text?: string;
}
export interface DatatableColumnIcon {
    class: string;
    placement?: 'left' | 'right' | 'top' | 'bottom';
}
export interface DatatableColumnSubIcon {
    class: string;
    placement?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
}
export interface DatatableColumnBorder {
    bottom?: boolean;
    left?: boolean;
    right?: boolean;
    top?: boolean;
}
export interface DatatableColumnNavigationRoute {
    routerLink: any[] | string;
    queryParams?: {
        [k: string]: any;
    };
}
export interface DatatableColumnFoldColumns {
    isFolded: boolean;
    columnNames: string[];
}
export interface DatatableColumn extends TableColumn {
    cellAlign?: 'left' | 'center' | 'right';
    cellBorder?: DatatableColumnBorder;
    colorRanges?: ColorRange[];
    format?: 'yyyy-MM-dd HH:mm:ss' | string | ((data: number) => string);
    isFolded?: boolean;
    foldColumns?: DatatableColumnFoldColumns;
    headerAlign?: 'left' | 'center' | 'right';
    headerBorder?: DatatableColumnBorder;
    headerIcon?: DatatableColumnIcon;
    headerSubIcon?: DatatableColumnSubIcon;
    noTextOverflow?: boolean;
    navigationRoute?: (...params: any) => DatatableColumnNavigationRoute;
    selectable?: boolean;
    staticCellTooltip?: DatatableColumnTooltip | DatatableColumnTooltip[];
    staticHeaderTooltip?: DatatableColumnTooltip | DatatableColumnTooltip[];
    translationKey?: string;
    translationValues?: {
        [key: string]: string;
    };
    uniqueCellClass?: string | ((data: any) => string | any);
}
